         "#text": "<html><head><title>{$mybb->settings['bbname']} - {$lang->profile}<\/title>\n{$headerinclude}\n<script type=\"text/javascript\" src=\"{$mybb->asset_url}/jscripts/report.js?ver=1804\"><\/script><script>\n\t$(document).ready(function(){\n\n\t$('ul.tabs li').click(function(){\n\t\tvar tab_id = $(this).attr('data-tab');\n\n\t\t$('ul.tabs li').removeClass('current');\n\t\t$('.tab-content2').removeClass('current');\n\n\t\t$(this).addClass('current');\n\t\t$(\"#\"+tab_id).addClass('current');\n\t})\n\n})\n\t<\/script><style>\n\t\t\t.container2{\n\t\t\tmargin: 0 auto;\n\t\t}\n\n\n\n\t\tul.tabs{\n\t\t\tmargin: 0px;\n\t\t\tpadding: 0px;\n\t\t\tlist-style: none;\n\t\t}\n\t\tul.tabs li{\n\t\t\tbackground: none;\n\t\t\tcolor: #222;\n\t\t\tdisplay: inline-block;\n\t\t\tpadding: 10px 15px;\n\t\t\tcursor: pointer;\n\t\t}\n\n\t\tul.tabs li.current{\n\t\t\tbackground: #ededed;\n\t\t\tcolor: #222;\n\t\t}\n\n\t\t.tab-content2{\n\t\t\tdisplay: none;\n\t\t\tbackground: #ededed;\n\t\t\tpadding: 15px;\n\t\t}\n\n\t\t.tab-content2.current{\n\t\t\tdisplay: inherit;\n\t\t}\n\t<\/style><\/head><body>\n{$header}\n<div class=\"float_right\" style=\"text-align: center\">{$buddy_options}{$ignore_options}{$report_options}<\/div><br /><br /><fieldset><table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\"><tr><td width=\"75%\"><span class=\"largetext\"><strong>{$formattedname}<\/strong><\/span><br /><span class=\"smalltext\">\n\t\t\t\t\t({$usertitle})<br />\n\t\t\t\t\t{$groupimage}\n\t\t\t\t\t{$userstars}<br /><br /><strong>{$lang->registration_date}<\/strong> {$memregdate}<br /><strong>{$lang->date_of_birth}<\/strong> {$membday} {$membdayage}<br /><strong>{$lang->local_time}<\/strong> {$localtime}<br /><strong>{$lang->postbit_status}<\/strong> {$online_status}\n\t\t\t\t<\/span><\/td><td width=\"25%\" align=\"right\" valign=\"middle\">{$avatar}<\/td><\/tr><\/table><\/fieldset><br /><div class=\"container2\"><ul class=\"tabs\"><li class=\"tab-link current\" data-tab=\"tab-1\"> Forum info<\/li><li class=\"tab-link\" data-tab=\"tab-2\">Contact details<\/li><li class=\"tab-link\" data-tab=\"tab-3\">Profile<\/li><li class=\"tab-link\" data-tab=\"tab-4\">Signature<\/li><li class=\"tab-link\" data-tab=\"tab-5\">Moderate<\/li><\/ul><div id=\"tab-1\" class=\"tab-content2 current\"><table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\"><tr><td width=\"50%\" valign=\"top\"><table border=\"0\" cellspacing=\"{$theme['borderwidth']}\" cellpadding=\"{$theme['tablespace']}\" class=\"tborder\"><tr><td colspan=\"2\" class=\"thead\"><strong>{$lang->users_forum_info}<\/strong><\/td><\/tr><tr><td class=\"trow1\" style=\"width: 30%;\"><strong>{$lang->joined}<\/strong><\/td><td class=\"trow1\">{$memregdate}<\/td><\/tr><tr><td class=\"trow2\"><strong>{$lang->lastvisit}<\/strong><\/td><td class=\"trow2\">{$memlastvisitdate}<\/td><\/tr><tr><td class=\"trow1\"><strong>{$lang->total_posts}<\/strong><\/td><td class=\"trow1\">{$memprofile['postnum']} ({$lang->ppd_percent_total}){$findposts}<\/td><\/tr><tr><td class=\"trow2\"><strong>{$lang->total_threads}<\/strong><\/td><td class=\"trow2\">{$memprofile['threadnum']} ({$lang->tpd_percent_total}){$findthreads}<\/td><\/tr><tr><td class=\"trow1\"><strong>{$lang->timeonline}<\/strong><\/td><td class=\"trow1\">{$timeonline}<\/td><\/tr>\n\t\t\t\t{$referrals}\n\t\t\t\t{$reputation}\n\t\t\t\t{$warning_level}\n\t\t\t<\/table><\/td><\/tr><\/table><\/div><div id=\"tab-2\" class=\"tab-content2\">\n\t\t{$contact_details}\n\t<\/div><div id=\"tab-3\" class=\"tab-content2\">\n\t\t{$profilefields}\n\t<\/div><div id=\"tab-4\" class=\"tab-content2\">\n\t\t{$signature}\n\t<\/div><div id=\"tab-5\" class=\"tab-content2\">\n\t\t{$modoptions}{$adminoptions}\n\t<\/div><\/div>\n\n\n\n{$footer}\n<\/body><\/html>"
